public class FileSelector {
    public static void main(String[] args) throws Exception {
        Scanner input = new Scanner(System.in);
        System.out.print("Please enter the full path of your file: ");
        String filePath = input.nextLine();

        // Create a new File object for the specified path
        File file = new File(filePath);

        // Check if the file exists
        if (!file.exists()) {
            System.err.println("The file does not exist!");
            return;
        }

        System.out.print("Please enter the full path of your destination folder: ");
        String destFolder = input.nextLine();

        // Create a new File object for the specified path
        File destDir = new File(destFolder);

        // Check if the directory exists
        if (!destDir.exists()) {
            System.err.println("The directory does not exist!");
            return;
        }

        // Copy the file to the destination folder
        boolean success = file.renameTo(new File(destDir, file.getName()));

        if (success) {
            System.out.println("File copied successfully.");
        } else {
            System.err.println("Failed to copy the file.");
        }
    }
}
