  const [count, setCount] = useState(0);

  // Hook para asegurarse de que el componente se actualice cada vez que count cambie
  useEffect(() => {
    console.log('El contador ha cambiado:', count);
  }, [count]);

  // Función de callback para manejar el evento del botón
  const handleClick = useCallback(() => {
    setCount(count + 1); // Incrementa el valor de count en cada clic
  }, []);

  return (
