  const [debuggingStatus, setDebuggingStatus] = useState('iniciales');

  useEffect(() => {
    console.log(`[useEffect]: count: ${count}, isFetching: ${isFetching}`);
  }, [count, isFetching]);

  // Función que se ejecuta cuando cambia el estado de debuggingStatus
  const handleDebuggingChange = (newStatus) => {
    setDebuggingStatus(newStatus);
    console.log(`[handleDebuggingChange]: new status:`, newStatus);

