using System;
using System.Collections.Generic;
using System.Linq;

public class FilteredTable<T>
{
    private List<T> _originalData = new List<T>();

    public IQueryable<T> GetFiltered(IQueryable<T> source, int id)
    {
        return source.Where(item => item.Id == id);
    }

    // Add a method to add data to the original list
    public void Add(T item)
    {
        _originalData.Add(item);
    }
}
