    Expression* expr1 = new Empty();
    Expression* expr2 = new Constant(5);
    Expression* expr3;

    // Evaluate each expression
    expr1->evaluate(); // Output: Empty expression
    expr2->evaluate(); // Output: Constant: 5

    delete expr1;      // Free the memory for the Empty object
