constexpr int calculate(int exp) {
    return exp == 0 ? 1 : (exp % 2 == 0 ? calculate(exp / 2) * calculate(exp / 2) : T(exp));
}

int main() {
    check_type<decltype(calculate(5))>(); // Comprobar que T es un entero
    constexpr auto result = calculate(5); // Resultado correcto para cualquier T
}
