{
    private readonly IService _service;

    // Constructor de la clase que recibe una instancia del objeto que será usado.
    public Component(IService service)
    {
        _service = service;
    }

    // El componente usa el servicio para obtener contenido.
    public string GetContent()
    {
        return _service.Get();
    }
