  const filePath = 'index.html';
  if (fs.existsSync(filePath)) {
    const fileStream = fs.createReadStream(filePath);
    res.setHeader('Content-Type', 'text/html');
    fileStream.pipe(res);
  } else {
    res.statusCode = 404;
    res.end();
  }
});

server.listen(3000, () => console.log('Server listening on port 3000'));
